mapper.select:
select wherereslut.* from( select rownum rnum, unionresult.* from ( select prod.lotnumber, prod.productname, prod.PARTNUMBER, pro.quantity, prod.spec, prod.unit, pro.makedate, pro.expdate from productinven pro join products prod on ( pro.productid = prod.productid )
union all
select mat.lotnumber, mat.materialname, mat.partnumber, inv.quantity, mat.spec, mat.unit, inv.receiptdate, inv.expdate from inventorystatus inv join materials mat on ( inv.materialid = mat.materialid ) ) unionresult ) wherereslut where rnum >= #{indexStart} and rnum <= #{indexEnd}
