mapper.select:
SELECT resulttt.* FROM ( SELECT rownum rnum, quresult.* FROM ( SELECT qu.*, users.username, pro.productname FROM qualitycontrols qu JOIN users ON (users.userid = qu.userid) JOIN products pro ON (pro.productid = qu.productid)
AND productname LIKE '%' || #{productname} || '%'
AND qualitycontroltime BETWEEN TO_DATE(#{searchDateStart}, 'yyyy-MM-dd"T"HH24:mi') AND TO_DATE(#{searchDateEnd}, 'yyyy-MM-dd"T"HH24:mi')
AND result LIKE #{result}
AND failreason LIKE '%' || #{failreason} || '%'
ORDER BY qu.qualitycontroltime DESC ) quresult ) resulttt WHERE rnum >= #{indexStart} AND rnum <= #{indexEnd}
